import React, { useEffect, useMemo, useState } from "react";
import "./App.css";

const clamp = (value, min = 0, max = 100) =>
  Math.min(max, Math.max(min, value));

const buildHistory = (base, variance) =>
  Array.from({ length: 14 }, () =>
    clamp(base + (Math.random() * variance - variance / 2), 5, 98)
  );

function App() {
  const [metrics, setMetrics] = useState(() => [
    {
      key: "cpu",
      label: "CPU Utilization",
      unit: "%",
      value: 37,
      history: buildHistory(42, 18),
    },
    {
      key: "memory",
      label: "Memory Usage",
      unit: "%",
      value: 62,
      history: buildHistory(58, 16),
    },
    {
      key: "disk",
      label: "Disk IO",
      unit: "%",
      value: 28,
      history: buildHistory(32, 20),
    },
    {
      key: "network",
      label: "Network",
      unit: "%",
      value: 44,
      history: buildHistory(40, 22),
    },
    {
      key: "latency",
      label: "P95 Latency",
      unit: "ms",
      value: 142,
      history: buildHistory(160, 70),
    },
    {
      key: "errors",
      label: "Error Rate",
      unit: "%",
      value: 0.7,
      history: buildHistory(0.9, 0.8),
    },
  ]);

  const [services, setServices] = useState([
    { name: "API Gateway", status: "operational", latency: 42 },
    { name: "Auth Service", status: "operational", latency: 55 },
    { name: "Billing", status: "degraded", latency: 88 },
    { name: "Notifications", status: "operational", latency: 63 },
  ]);

  useEffect(() => {
    const id = setInterval(() => {
      setMetrics((current) =>
        current.map((metric) => {
          const jitter = metric.unit === "ms" ? 25 : 4;
          const next = clamp(
            metric.value + (Math.random() * (jitter * 2) - jitter),
            0,
            metric.unit === "ms" ? 400 : 100
          );
          const history = [...metric.history.slice(1), next];
          return {
            ...metric,
            value: Number(next.toFixed(metric.unit === "ms" ? 0 : 1)),
            history,
          };
        })
      );

      setServices((current) =>
        current.map((service) => {
          const nextLatency = clamp(
            service.latency + (Math.random() * 18 - 9),
            30,
            220
          );
          const status = nextLatency > 150 ? "degraded" : "operational";
          return {
            ...service,
            latency: Number(nextLatency.toFixed(0)),
            status,
          };
        })
      );
    }, 1800);

    return () => clearInterval(id);
  }, []);

  const alerts = useMemo(
    () => [
      {
        severity: "warning",
        title: "Node cpu-high",
        detail: "node-3 reported 72% CPU for 5m",
      },
      {
        severity: "info",
        title: "Deploy",
        detail: "frontend v1.4.2 rolled out",
      },
      {
        severity: "critical",
        title: "Error spike",
        detail: "auth service 1.3% error rate",
      },
    ],
    []
  );

  const clusters = useMemo(
    () => [
      { name: "eks-prod", nodes: 8, pods: 116, utilization: 63 },
      { name: "eks-staging", nodes: 4, pods: 52, utilization: 48 },
      { name: "eks-dev", nodes: 3, pods: 31, utilization: 37 },
    ],
    []
  );

  const regions = useMemo(
    () => [
      { name: "us-east-1", traffic: 42 },
      { name: "us-west-2", traffic: 33 },
      { name: "eu-central-1", traffic: 19 },
      { name: "ap-south-1", traffic: 15 },
    ],
    []
  );

  return (
    <div className="app">
      <header className="header">
        <div>
          <p className="eyebrow">Cloud Native SRE</p>
          <h1 className="title">System Monitoring Dashboard</h1>
          <p className="subtitle">
            Live service health, resource pressure, and regional signals.
          </p>
        </div>
        <div className="header-actions">
          <span className="badge badge-live">Live</span>
          <span className="badge badge-healthy">Cluster stable</span>
        </div>
      </header>

      <section className="grid">
        {metrics.map((metric) => (
          <div key={metric.key} className="card">
            <div className="card-top">
              <p className="label">{metric.label}</p>
              <span
                className={`chip ${
                  metric.value > 80 && metric.unit === "%"
                    ? "chip-warn"
                    : "chip-ok"
                }`}
              >
                {metric.unit === "ms" ? "Latency" : "Usage"}
              </span>
            </div>
            <div className="metric">
              <span className="metric-value">{metric.value}</span>
              <span className="metric-unit">{metric.unit}</span>
            </div>
            <div className="spark">
              {metric.history.map((point, idx) => (
                <span
                  key={idx}
                  className="spark-bar"
                  style={{ height: `${clamp(point, 5, 100)}%` }}
                />
              ))}
            </div>
            <div className="progress">
              <div
                className="progress-fill"
                style={{
                  width: `${
                    metric.unit === "ms"
                      ? clamp(metric.value / 4, 0, 100)
                      : metric.value
                  }%`,
                }}
              />
            </div>
          </div>
        ))}
      </section>

      <section className="layout">
        <div className="panel">
          <div className="panel-head">
            <h3>Service Health</h3>
            <span className="hint">Auto-refreshed</span>
          </div>
          <div className="service-list">
            {services.map((service) => (
              <div key={service.name} className="service-row">
                <div className="service-id">
                  <span className={`dot dot-${service.status}`} />
                  <span>{service.name}</span>
                </div>
                <div className="service-meta">
                  <span className="pill">{service.latency} ms</span>
                  <span className={`pill pill-${service.status}`}>
                    {service.status}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="panel">
          <div className="panel-head">
            <h3>Recent Alerts</h3>
            <span className="hint">Past 30m</span>
          </div>
          <div className="alerts">
            {alerts.map((alert, idx) => (
              <div key={idx} className="alert">
                <span className={`pill pill-${alert.severity}`}>
                  {alert.severity}
                </span>
                <div>
                  <p className="alert-title">{alert.title}</p>
                  <p className="alert-detail">{alert.detail}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="layout">
        <div className="panel">
          <div className="panel-head">
            <h3>Clusters</h3>
            <span className="hint">EKS</span>
          </div>
          <div className="cluster-grid">
            {clusters.map((cluster) => (
              <div key={cluster.name} className="cluster">
                <div className="cluster-top">
                  <span className="cluster-name">{cluster.name}</span>
                  <span className="pill">{cluster.nodes} nodes</span>
                </div>
                <p className="cluster-sub">{cluster.pods} pods running</p>
                <div className="progress small">
                  <div
                    className="progress-fill"
                    style={{ width: `${cluster.utilization}%` }}
                  />
                </div>
                <p className="cluster-foot">
                  Utilization {cluster.utilization}%
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="panel">
          <div className="panel-head">
            <h3>Traffic by Region</h3>
            <span className="hint">Requests %</span>
          </div>
          <div className="region-list">
            {regions.map((region) => (
              <div key={region.name} className="region-row">
                <span>{region.name}</span>
                <div className="progress tiny">
                  <div
                    className="progress-fill"
                    style={{ width: `${region.traffic}%` }}
                  />
                </div>
                <span className="region-value">{region.traffic}%</span>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
}

export default App;
